mapper.select:
select cph,vehid from (SELECT Longitude, Latitude, begintime, endtime, vehid, platecolor, cph, CONVERT (VARCHAR(100), begintime, 23) tonjishijian, CONCAT ( ( floor( datediff(SECOND, begintime, endtime) / 86400 ) ), '天', ( floor( datediff(SECOND, begintime, endtime) / 3600 ) % 24 ), '时', ( floor( datediff(SECOND, begintime, endtime) / 60 ) % 60 ), '分', ( datediff(SECOND, begintime, endtime) % 60 ), '秒' ) AS tingcheshijian, datediff(SECOND, begintime, endtime) AS times FROM ( SELECT a.*, ( SELECT MIN (TIME) FROM dyndata WHERE vehid = a.vehid AND TIME >= #{begintime} AND TIME <= #{endtime} AND velocity = 0 AND Latitude = a.Latitude AND Longitude = a.Longitude ) begintime, ( SELECT MAX (TIME) FROM dyndata WHERE vehid = a.vehid AND TIME >= #{begintime} AND TIME <= #{endtime} AND velocity = 0 AND Latitude = a.Latitude AND Longitude = a.Longitude ) endtime FROM ( SELECT vehicle.id vehid, Longitude, Latitude, platecolor, cph FROM dyndata, vehicle WHERE vehicle.id = dyndata.vehid AND vehid in(
select id from Vehicle where userid=( select id from Client where khqc=#{company}
)
) AND TIME >= #{begintime} AND TIME <= #{endtime} AND velocity = 0 GROUP BY Longitude, Latitude, vehicle.id, platecolor, cph ) a ) a WHERE datediff(SECOND, begintime, endtime) >= 600
) a GROUP BY cph,vehid
