mapper.select:
select cheliangpaizhao from anbiao_vehicle
where cheliangpaizhao=#{cheliangpaizhao}
and chepaiyanse=#{chepaiyanse}
and ifnull(is_deleted,0) = 0
and ifnull(cheliangzhuangtai,0) != 2
LIMIT 1;

