mapper.sql:
SELECT a.yujingxiang as tixingxiangqing,IFNULL(b.counts,0) as counts,b.tongjiriqi,b.deptId,b.tixingleixing,a.shuoming as shuoming, a.url,a.id as tixingxiangqingid FROM anbiao_yujingxiang a LEFT JOIN ( SELECT count(a.tixingxiangqingid) AS counts, a.tongjiriqi, a.dept_id AS deptId, a.tixingleixing, a.tixingxiangqingid FROM anbiao_jinritixing_jiesuan a inner join blade_dept b on a.dept_id = b.id WHERE 1=1 and ifnull(a.isdelete,0) = 0
AND tongjiriqi = #{tongjiriqi}
AND tixingleixing = #{tixingleixing}
AND a.dept_id = #{deptId}
and a.tixingxiangqingid in( SELECT a.yujingxiangid FROM anbiao_yujingquanxian a WHERE a.postid = #{postid} )
GROUP BY deptId, tongjiriqi, a.tixingleixing, a.tixingxiangqingid ) b on a.id = b.tixingxiangqingid

mapper.select:
SELECT
count(1)
FROM
anbiao_jinritixing_jiesuan a
inner join  blade_dept b
on a.dept_id = b.id
WHERE 1=1 and ifnull(a.isdelete,0) = 0

    AND tongjiriqi = #{tongjiriqi}


    AND tixingleixing = #{tixingleixing}


    AND a.dept_id = #{deptId}


    and a.tixingxiangqingid in(
    SELECT a.yujingxiangid FROM anbiao_yujingquanxian a WHERE a.postid = #{postid}
    )

mapper.sql:
        SELECT a.tixingleixing,a.tixingxiangqing,d.dept_name as deptName,b.cheliangpaizhao,b.chepaiyanse,c.jiashiyuanxingming from anbiao_jinritixing_jiesuan a
LEFT JOIN anbiao_vehicle b on a.cheliangid = b.id and ifnull(b.is_deleted,0) = 0
LEFT JOIN anbiao_jiashiyuan c on a.jiashiyuanid = c.id and ifnull(c.isdelete,0) = 0
LEFT JOIN blade_dept d on a.dept_id = d.id  where ifnull(a.isdelete,0) = 0

            AND a.dept_id  = #{deptId}


            AND a.tongjiriqi = #{tongjiriqi}
